mapper.select:
SELECT COUNT(*)
FROM member
WHERE userId=#{userId} AND userPw=#{userPw}

SELECT COUNT(*)
FROM blacklist
WHERE userNo=#{userNo}

SELECT COUNT(*)
FROM member
WHERE suserno=#{suserno}

mapper.update:
UPDATE member
SET userpw=#{userPw}, usernick=#{userNick}, email=#{email}, address=#{address}, zipcode=#{zipCode}, detailaddress=#{detailaddress }, latitude=#{latitude}, longitude=#{longitude}, phone=#{phone}
WHERE userno=#{userNo}

mapper.select:
SELECT * FROM pet
WHERE userNo=#{userNo}
ORDER BY petno

SELECT * FROM petfile
WHERE petNo=#{petNo}
ORDER BY petno

select count(*)
from hospital
where userno = #{userNo}

select hospitalNo
from hospital
where userNo = #{userNo}

mapper.update:
UPDATE member
SET userPw=#{userPw}
WHERE userNo=#{userNo}

update Pet 
